import React,  { Component } from 'react';
import { Button, View, Text } from 'react-native';
import obtenerCategorias from '../librarySupermercadoCategoria';


export class StackCategoriasScreen extends Component{

    constructor(props){
        super(props);
        this.state={
            categorias: [],
            cargando: true,
        }
    
        this.handlerobtenerCategorias = this.handlerobtenerCategorias.bind(this); 
        this.renderCategorias = this.renderCategorias.bind(this); 

    }  

    handlerobtenerCategorias(){
        
        obtenerCategorias().then((infoCategoria) => {
           const param = infoCategoria.data;
            this.renderCategorias(param);
            
        }).catch((err) => {
            this.setState({
                cargando: false,
            });
        }).catch((err) => {
            console.log(err); 
        });
    }

    /*this.state.categorias[0].map((categoria) => {
            x++;
           return (
                    <Text key={1}> LA {x} </Text>
            );
        });*/

    /*
        let categories = infoCategoria;
            categories.forEach(element => {
                this.setState(
                    prevState => ({
                    categorias: [...prevState.arrayvar, element],
                    cargando: false
                    })
                );    
            });
   
    renderCategorias(infoCategorias) {
        return infoCategorias.map((day) => {
            return (
                <Text> hola </Text>
            );
        });   
    }  
    */

    renderCategorias(infoCategorias) {
            return infoCategorias.map((day) => {
                return (
                    <Text> {day.nombre} </Text>
                );
            });
    }  


                            //onPress={(categoria) => { navigation.navigate('Details') }

    render(){
        if(this.state.cargando == true){
            return(

                <View style={{ flex: 1, justifyContent: 'center',  alignItems: 'center' }}>
                    <Text>CARGANDO {this.handlerobtenerCategorias()}</Text>
                </View>
                
            );
        } else {
            return(
                <View style={{ flex: 1, justifyContent: 'center',  alignItems: 'center' }}>
                    {this.renderCategorias()}
                </View>

            );
        }
    }  

    


}
