import React,  { Component } from 'react';
import { Button, View, Text } from 'react-native';
import obtenerCategorias from '../librarySupermercadoCategoria';


export class StackCategoriasScreen extends Component{

    constructor(props){
        super(props);
        this.state={
            nombres: [],
            encontrado: true,
            unaVez: false,
        }
 
        this.handlerobtenerArticulo = this.handlerobtenerArticulo.bind(this); 
    }  

    handlerobtenerCategorias(){
        obtenerCategorias().then((infoCategoria) => {
            this.setState({ encontrado: true,
                            nombre: infoCategoria.data.nombre,
                            });
        }).catch((err) => {
            console.log(err);
            this.setState({     
                encontrado: false,
            }); 
        });
    }


    renderCategorias() {
        this.handlerobtenerCategorias().then(() => {
            return this.state.nombre.map((categoria) => {
                return (
                    <View>
                        <Button
                            onPress={(categoria) => { navigation.navigate('Details'); return (categoria);} }
                            title={categoria}
                            color="#990"
                        />
                    </View>
                );
            });
        }).catch((err) => {
            console.log(err);
            return(
                <View>
                    <Text>ERROR</Text>
                </View>
            );
        });
    }  


    render(){

        return(
            this.renderCategorias()
        );
    
    
    }  

    


}
