const axios = require("axios");
const params = new URLSearchParams();

function obtenerArticulo(nroIdArticulo){
    params.append(nroIdArticulo);
    return axios.get("http://192.168.1.5:80/api-supermercado/articulos/id/" + params);
}

module.exports = obtenerArticulo;
