const axios = require("axios");
var URL;

function obtenerArticulo(nroIdArticulo){
    URL = "http://192.168.1.5:9999/api-supermercado/articulos/id/" + nroIdArticulo;
    console.log(URL);
    return axios.get("http://192.168.1.5:9999/api-supermercado/articulos/id/" + nroIdArticulo );
}

module.exports = obtenerArticulo;
