import React from 'react';
import { StyleSheet, Button, Text, TextInput, View } from 'react-native';
import obtenerArticulo from '../library';
import storeArticulo from '../libraryUsuario';

export class RegistrarGastos extends React.Component {

    constructor(props){
        super(props);
        this.state={
            id: "",
            nombre: "",
            precio: "",
            marca: "",
            categoria: "",
            encontrado: true,
        }
 
        this.handlerObtenerId = this.handlerObtenerId.bind(this);
        this.handlerobtenerArticulo = this.handlerobtenerArticulo.bind(this); 
        this.handlerStoreArticulo = this.handlerStoreArticulo.bind(this); 

    }  

    
        handlerObtenerId(event){
            this.setState({id: event.target.value});
        }

   
        handlerobtenerArticulo(){
            obtenerArticulo(this.state.id).then((info) => {
             this.setState({ encontrado: true,
                                nombre: info.data.nombre,
                                precio: info.data.precio,
                                })
             
             }).catch((err) => {
                console.log(err);
                this.setState({     
                    encontrado: false
                });
            });

        }

        handlerStoreArticulo(){
            storeArticulo(this.state.nombre, this.state.precio, " ", " ");
        }


        render(){
           
            if(this.state.encontrado === true){
                this.handlerStoreArticulo();
                return (
                    <View style={styles.container}>
                        <View>
                            <Text style={styles.peticiones} h1>{this.state.nombre}</Text>
                            <Text style={styles.peticiones} h1>{this.state.precio}</Text>
                        </View>
                        <View>
                            <Text style={styles.titulo}> Ingrese numero de Registro </Text>
                            <TextInput
                                editable
                                keyboardAppearance={'light'}
                                maxLength={4}  
                                style={styles.cuadrado}
                                onChange={this.handlerObtenerId}
                                numberOfLines={4}
                            />
                            <Button
                                onPress={this.handlerobtenerArticulo}
                                title="Apretar"
                                color="#990"
                            />
                        </View>
                    </View>
                );
            } else {
                return (
                    <View style={styles.container}>
                        <Text style={styles.danger}>Numero de Registro INVALIDO</Text>

                        <View>
                            <Text style={styles.titulo}> Ingrese numero de Registro </Text>
                            <TextInput
                                editable
                                keyboardAppearance={'light'}
                                maxLength={4}  
                                style={styles.cuadrado}
                                onChange={this.handlerObtenerId}
                                numberOfLines={4}
                            />
                            <Button
                                onPress={this.handlerobtenerArticulo}
                                title="Apretar"
                                color="#990"
                            />
                            <Text style={styles.aclaracion}> Existen a partir del numero 1 al numero 3944</Text>
                        </View>
                    </View>
                );
            }
        }
}

const styles = StyleSheet.create({
    container: {
        backgroundColor: '#222',
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
    },
    cuadrado: {
        height: 40, 
        borderColor: 'gray', 
        borderWidth: 2,
        margin: 10,
        textAlign: 'center',
    },
    peticiones: {
      color: 'white',
    },
    titulo: {
      color: '#099',
    },
    aclaracion: {
      color: 'grey'
    },
    danger: {
        color: 'red',
        fontSize: 20
    }
  });
