import React from 'react';
import { StyleSheet, Button, Text, TextInput, View } from 'react-native';
import mysql from 'mysql';

const obtenerArticulo = require("../library.js");

const connection = mysql.createPool({
    host     : 'localhost', // Your connection adress (localhost).
    user     : 'root',     // Your database's username.
    password : '',        // Your database's password.
    database : 'api_supermercado'   // Your database's name.
  });
  
  // Starting our app.
  const app = express();
  
  // Creating a GET route that returns data from the 'users' table.
  app.get('/users', function (req, res) {
      // Connecting to the database.
      connection.getConnection(function (err, connection) {
  
      // Executing the MySQL query (select all data from the 'users' table).
      connection.query('SELECT * FROM articulos', function (error, results, fields) {
        // If some error occurs, we throw an error.
        if (error) throw error;
  
        // Getting the 'response' from the database and sending it to our route. This is were the data is.
        res.send(results)
      });
    });
  });
  
  // Starting our server.
  
  

export class RegistrarGastos extends React.Component {

    constructor(props){
        super(props);
        this.state={
            id: "",
            nombre: "",
            precio: "",
            id_marca: "",
            encontrado: true,
            texto: "",
        }
 
        this.handlerObtenerId = this.handlerObtenerId.bind(this);
        this.handlerobtenerArticulo = this.handlerobtenerArticulo.bind(this);    
        this.test = this.test.bind(this);    
    }  
    

        test(){
            fetch('http://127.0.0.1:3306')
            .then(response => response.json())
            .then(users => console.warn(users))
        }
        

    
        handlerObtenerId(event){
            this.setState({id: event.target.value});
        }
   
        handlerobtenerArticulo(){
            obtenerArticulo(this.state.id).then((info) => {
             console.log(info)
             this.setState({ encontrado: true,
                                nombre: info.data.nombre,
                                precio: info.data.precio,
                                })
             }).catch((err) => {
                console.log(err);
                this.setState({     
                    encontrado: false
                });
            });
        }

        render(){
           
            if(this.state.encontrado === true){
                return (
                    <View style={styles.container}>
                        <View>
                            <Text style={styles.peticiones} h1>{this.state.nombre}</Text>
                            <Text style={styles.peticiones} h1>{this.state.precio}</Text>
                            <Text h1> {test()}</Text>
                        </View>
                        <View>
                            <Text style={styles.titulo}> Ingrese numero de Registro </Text>
                            <TextInput
                                editable
                                keyboardAppearance={'light'}
                                maxLength={4}  
                                style={styles.cuadrado}
                                onChange={this.handlerObtenerId}
                                numberOfLines={4}
                            />
                            <Button
                                onPress={this.handlerobtenerArticulo}
                                title="Apretar"
                                color="#990"
                            />
                        </View>
                    </View>
                );
            } else {
                return (
                    <View style={styles.container}>
                        <Text style={styles.danger}>Numero de Registro INVALIDO</Text>

                        <View>
                            <Text style={styles.titulo}> Ingrese numero de Registro </Text>
                            <TextInput
                                editable
                                keyboardAppearance={'light'}
                                maxLength={4}  
                                style={styles.cuadrado}
                                onChange={this.handlerObtenerId}
                                numberOfLines={4}
                            />
                            <Button
                                onPress={this.handlerobtenerArticulo}
                                title="Apretar"
                                color="#990"
                            />
                            <Text style={styles.aclaracion}> Existen a partir del numero 1 al numero 3944</Text>
                        </View>
                    </View>
                );
            }
        }
}

const styles = StyleSheet.create({
    container: {
        backgroundColor: '#222',
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
    },
    cuadrado: {
        height: 40, 
        borderColor: 'gray', 
        borderWidth: 2,
        margin: 10,
        textAlign: 'center',
    },
    peticiones: {
      color: 'white',
    },
    titulo: {
      color: '#099',
    },
    aclaracion: {
      color: 'grey'
    },
    danger: {
        color: 'red',
        fontSize: 20
    }
  });
