import React from 'react';
import { StyleSheet, Button, Text, TextInput, View, addons } from 'react-native';
import obtenerArticulo from '../librarySupermercado';
import storeArticulo from '../libraryUsuario';

export class RegistrarGastos extends React.Component {

    constructor(props){
        super(props);
        this.state={
            textInput: [],
            nombre: "",
            precio: "",
            marca: "",
            categoria: "",
            encontrado: true,
            unaVez: false,
        }
 
        this.handlerObtenerId = this.handlerObtenerId.bind(this);
        this.handlerobtenerArticulo = this.handlerobtenerArticulo.bind(this); 

    }  

    
        handlerObtenerId(event){
            this.setState({id: event.target.value});
        }

   
        handlerobtenerArticulo(){
            obtenerArticulo(this.state.id).then((info) => {
             /*this.setState({ encontrado: true,
                                nombre: info.data.nombre,
                                precio: info.data.precio,
                                unaVez: true,
                                })
             */
                 storeArticulo(info.data.nombre, info.data.precio, 0, "0");
             }).catch((err) => {
                console.log(err);
                this.setState({     
                    encontrado: false
                });
            });

        }


        render(){
            const { textInput } = this.state;
            if(this.state.encontrado === true){
                return (
                    <View style={styles.form_Container}>
                      {data.map((_objeto, i) => (
                        <View key={"n" + i} style={styles.dateD_Info}>
                              <View key={"x" + i}>
                                <TextInput
                                  style={styles.textbox3}
                                  keyboardType="phone-pad"
                                  placeholder=""
                                  editable={true}
                                  maxLength={5}
                                  placeholderTextColor="#0891eb"
                                  onChangeText=
                                  {value => {
                                    const newTextInputState = textInput;
                                    newTextInputState[i] = value;
                                    this.setState({textInput: newTextInputState});
                                  }}
                                    value={textInput[i]}
                                />
                              </View>
                        </View>
                      ))}
                    </View>
                );
                
                 
            } else {
                return (
                    <View style={styles.container}>
                        <Text style={styles.danger}>Numero de Registro INVALIDO</Text>
                        <Text style={styles.danger}h1> {this.state.id} </Text>
                        <View>
                            <Text style={styles.titulo}> Ingrese numero de Registro </Text>
                            <TextInput
                                editable
                                keyboardAppearance={'light'}
                                maxLength={4}  
                                style={styles.cuadrado}
                                onPress={this.handlerObtenerId}
                                numberOfLines={4}
                            />
                            <Button
                                onPress={this.handlerobtenerArticulo}
                                title="Apretar"
                                color="#990"
                            />
                            <Text style={styles.aclaracion}> Existen a partir del numero 1 al numero 3944</Text>
                        </View>
                    </View>
                );
            }
        }
}

const styles = StyleSheet.create({
    container: {
        backgroundColor: '#222',
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
    },
    cuadrado: {
        height: 40, 
        borderColor: 'gray', 
        borderWidth: 2,
        margin: 10,
        textAlign: 'center',
    },
    peticiones: {
      color: 'white',
    },
    titulo: {
      color: '#099',
    },
    aclaracion: {
      color: 'grey'
    },
    danger: {
        color: 'red',
        fontSize: 20
    }
  });
